import React, { Component } from 'react';
import Navigation from './Navigation';
import savings from '../assets/savings.png';
import '../styles/Savings.css';
import data from '../wtso.json';
const axios = require('axios')
const cheerio = require('cheerio');


class Savings extends Component {

    constructor(props){
        super();
        this.state = {
            wtso: '',
            wtsoImg:''
        }
    }
    componentDidMount(){
        this.setState({wtso: data.text})
        console.log(this.state.wtso);
        if(this.state.wtso.includes("Right now on WTSO:")){
            console.log("CONTAINS");
            this.setState({wtso: this.state.wtso.replace("Right now on WTSO:", "")})
        }
        axios
	.get('https://cors-anywhere.herokuapp.com/https://www.wtso.com/')
	.then((response) => {
		const $ = cheerio.load(response.data);
		// let title = $("title").text();
		// console.log(title);
        console.log($('#current-offer-bottle-image').attr().src);
        this.setState({wtsoImg: $('#current-offer-bottle-image').attr().src});
	})
	.catch((error) => {
		console.error(error)
	});
    }


    render() {
        let header = "SAVINGS";
        return (
            <div>
                <Navigation header={header} />
                <img id="savings" src={savings} className="pageImg"/>
             <p>{this.state.wtso}</p>
             <img src={this.state.wtsoImg} />
            </div>
        );
    }
}

export default Savings;
