import { useState } from 'react';

export const useForm = (callback, initialState = {}) => {
    const [values, setValues] = useState(initalState);

    const onChange = (e) => {
        setValues({...values, [e.target.name]: e.target.value});
    }

    const onSubmit = e => {
        e.preventDefault();
        callback();
    }

    return {
        onChange,
        onSubmit,
        values
    }
}
