};

const UPDATE_ZIPCODE = gql`

mutation updateZipcode(
 $email: String!
 $zipCode: String!
) {
    update_zipcode(
            updateZipcode: {
                email: $email,
                zipCode: $zipCode
            }
        ){
            id email token firstName lastName zipCode createdAt
        }
}
`;

export default Account;
