import React, { useState } from 'react';
import Navigation from './Navigation';
import '../styles/Account.css';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
const useStyles = makeStyles((theme) => ({
    root: {
      '& > *': {
        margin: theme.spacing(1),
        width: '25ch',
      },
    },
  }));
const Account = () => {
    //If no free trial, let them cancel free trial
    //Show X on cancel button
    const [email, setEmail] = useState("kylelong9506@gmail.com");
    const [joinDate, setJoinDate] = useState("3/21/2021");
    const [cancel, setCancel] = useState(false);
    const handleCancel = () => {
        setCancel(!cancel);
    }
    const handleOptions = (e) => {
        const { name } = e.target;
        if(name === "no"){
            setCancel(false);
        } else if(name == "yes"){
            //Cancel subscription : update user status to canceled
            //Set 30 to delete account, do by hand if it is too taxing technically at first
        }
    }
    const onChange = () => {

    }
    return (
        <div>
             <Navigation />
             <div className="accountContainer">
                 <h3 className="header">Account Information</h3>
                 <p>Email: {email}</p>
                 <p>Member since: {joinDate}</p>
                 <form className={classes.root} noValidate autoComplete="off" id="loginForm">
      <TextField id="outlined-basic" label="Email" variant="outlined" name="zipcdoe" inputProps={{ maxLength:5 }} onChange={onChange} />
        <div className="buttons">
          <button className="button is-info is-light is-medium is-fullwidth" >Update</button>
        </div>


    </form>
                 <div class="buttons">
                    <button class="button is-danger is-light" id="cancel" onClick={handleCancel}>Cancel Subscription</button>
                </div>
                {cancel && 
                        <div className="accountContainer"> 
                            <h3>Are you sure?</h3>
                            <p>You have 14 days left in your feel trial. Stick around to enjoy it.</p>
                            <p>Otherwise, your account will be deleted in 30 days.</p>
                            <div class="buttons">
                                <button class="button is-info is-light" name="yes" onClick={handleOptions}>Yes</button>
                                <button class="button is-info is-light" name="no" onClick={handleOptions}>No</button>
                            </div>
                        </div>
                    }
             </div>
        </div>
    );
};

export default Account;
